var cache = {};
export function getLiteralText(text) {
    if (cache[text]) {
        return cache[text];
    }
    var el = document.createElement('div');
    el.innerHTML = text;
    cache[text] = el.textContent;
    return cache[text];
}
var keys = ['&nbsp;', '&lt;', '&gt;', '&amp;', '&apos;', '&ensp;', '&emsp;'];
var replaceEncodeReg = new RegExp(keys.join('|'), 'g');
export function decode(str) {
    return str.replace(replaceEncodeReg, function (m) { return getLiteralText(m); });
}
