import { existsSync, readFileSync } from 'fs';
import postcss from 'postcss';
import { getPath } from './helpers';
export var walk = function (filePath, cssPaths) {
    if (!existsSync(filePath)) {
        return;
    }
    cssPaths.add(filePath);
    var content = readFileSync(filePath);
    var ast = postcss.parse(content);
    if (!ast.nodes) {
        return;
    }
    ast.nodes.forEach(function (node) {
        if (node.type === 'atrule' && node.name === 'import') {
            var file = getPath(filePath, node.params.replace(/'|"/g, ''));
            if (!existsSync(file)) {
                console.error("\u6587\u4EF6 " + file + " \u4E0D\u5B58\u5728");
                return;
            }
            walk(file, cssPaths);
        }
    });
};
export default function style(platformConfig, id) {
    var cssPaths = new Set();
    var filePath = id.replace(/\.js$/, platformConfig.style);
    walk(filePath, cssPaths);
    return Array.from(cssPaths);
}
